import React, { Component } from 'react';
import Form from "react-bootstrap/Form"
import Button from "react-bootstrap/Button"
import { Alert, Container } from 'react-bootstrap';

class AddProduct extends Component {
    constructor(props) {
        super(props)

        this.state = {
            categories: [],
            category_id: "",
            product_name: "",
            description: "",
            price: "",
            image: "",
            variant: "",
            message: "",
            added: false
        }

        this.setCategoryID = this.setCategoryID.bind(this);
        this.setProductName = this.setProductName.bind(this);
        this.setDescription = this.setDescription.bind(this);
        this.setPrice = this.setPrice.bind(this);
        this.setImage = this.setImage.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.setAdded = this.setAdded.bind(this);
    }

    componentDidMount() {
        const token = this.props.token

        fetch("http://localhost:8000/categories", {
            method: 'GET',
            headers: {
                'accept': 'application/json',
                'Content-Type': 'application/json',
                Authorization: "Bearer " + token.replace(/^"(.*)"$/, '$1')
            },
        })
            .then(async response => {
                const data = await response.json();

                if (!response.ok) {
                    const error = (data && data.message) || response.statusText;
                    return Promise.reject(error);
                }
                this.setState({ categories: data })
            })
            .catch(error => {
                this.setState({ errorMessage: error.toString() });
                console.error('There was an error!', error);
            });
    }

    setProductName(event) {
        this.setState({ name: event.target.value });
    }

    setDescription(event) {
        this.setState({ description: event.target.value });
    }

    setCategoryID(event) {
        this.setState({ category_id: event.target.value });
    }

    setImage(event) {
        this.setState({ image: event.target.value });
    }

    setPrice(event) {
        this.setState({ price: event.target.value });
    }

    setAdded(value) {
        this.setState({ added: value });
    }

    handleSubmit(event) {
        event.preventDefault()
        this.addProduct({ name: this.state.product_name, 
                           description: this.state.description,
                           category_id: this.state.category_id,
                           image: this.state.image,
                           price: this.state.price
                        });
    }

    async addProduct(details) {
        const token = this.props.token
        const qs = require('qs');
        return fetch('http://localhost:8000/product/add', {
            method: 'POST',
            headers: {
                'Content-Type': 'multipart/form-data',
                Authorization: "Bearer " + token.replace(/^"(.*)"$/, '$1')
            },
            body: qs.stringify(details)
        })
            .then(response => {
                if (!response.ok) {
                    this.setState({ added: true });
                    this.setState({ variant: "danger" })
                    return response.json();
                }
                else {
                    this.setState({ added: true });
                    this.setState({ variant: "success" })
                    this.setState({ product_name: "" })
                    this.setState({ description: "" })
                    return response.json();
                }
            })
            .then(data => {
                this.setState({ message: data.detail })

            })
            .catch(error => {
                this.setState({ errorMessage: error.toString() });
                console.error('There was an error!', error);
            });
    }

    render() {
        return (
            <div className="Login">
                <Container fluid="md">
                    <Form onSubmit={this.handleSubmit}>
                        <Form.Group size="lg" controlId="product_name">
                            <Form.Label> Product Name </Form.Label>
                            <Form.Control
                                autoFocus
                                type="text"
                                value={this.state.product_name}
                                onChange={this.setProductName}
                            />
                        </Form.Group>

                        <Form.Group size="lg" controlId="product_description">
                            <Form.Label> Description </Form.Label>
                            <Form.Control
                                type="text"
                                value={this.state.description}
                                onChange={this.setDescription}
                            />
                        </Form.Group>
                        <Form.Group size="lg" controlId="product_description">
                            <Form.Label> Category </Form.Label>
                            <Form.Control
                                required
                                type="text"
                                as="select"
                                onChange={this.setCategoryID}
                                value = {this.state.category_id}
                            >
                                {this.state.categories.map((category) => (
                                    <option key={category.id} value={category.id}>
                                        {category.name}
                                    </option>
                                ))}
                            </Form.Control>
                        </Form.Group>
                        <Form.Group controlId="formFileSm" className="mb-3">
                            <Form.Label>Choose Image</Form.Label>
                            <Form.Control type="file" size="lg" />
                        </Form.Group>

                        <Button block type="submit" disabled={!(this.state.product_name && this.state.description)}>
                            Save Details
                        </Button>
                    </Form>

                    <Alert show={this.state.added} variant={this.state.variant}>
                        <Alert.Heading>{this.state.message}</Alert.Heading>
                        <hr />
                        <div className="d-flex justify-content-end">
                            <Button onClick={() => this.setAdded(false)} variant="outline-success">
                                Close!
                            </Button>
                        </div>
                    </Alert>

                </Container>
            </div>
        );
    }
}

export default AddProduct;
