import React, { Component } from 'react';
import Form from "react-bootstrap/Form"
import Button from "react-bootstrap/Button"
import { Alert, Container } from 'react-bootstrap';

class AddProduct extends Component {
    constructor(props) {
        super(props)

        this.state = {
            category_id: "",
            product_name: "",
            description: "",
            price: "",
            image: "",
            variant: "",
            message: ""
        }

        this.setCategoryID = this.setCategoryID.bind(this);
        this.setProductName = this.setProductName.bind(this);
        this.setDescription = this.setDescription.bind(this);
        this.setPrice = this.setPrice.bind(this);
        this.setImage = this.setImage.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.setAdded = this.setAdded.bind(this);
    }

    setProductName(event) {
        this.setState({ name: event.target.value });
    }

    setDescription(event) {
        this.setState({ description: event.target.value });
    }

    setCategoryID(event) {
        this.setState({ category_id: event.target.value });
    }

    setImage(event) {
        this.setState({ image: event.target.value });
    }
    
    setPrice(event){
        this.setState({ price: event.target.value });
    }
    
    setAdded(value) {
        this.setState({ added: value });
    }

    handleSubmit(event) {
        event.preventDefault()
        this.addCategory({ name: this.state.category_name, description: this.state.description });
    }

    async addCategory(details) {
        const token = this.props.token

        return fetch('http://localhost:8000/category/add', {
            method: 'POST',
            headers: {
                'accept': 'application/json',
                'Content-Type': 'application/json',
                Authorization: "Bearer " + token.replace(/^"(.*)"$/, '$1')
            },
            body: JSON.stringify(details)
        })
            .then(response => {
                if (!response.ok) {
                    this.setState({ added: true });
                    this.setState({ variant: "danger" })
                    return response.json();
                }
                else {
                    this.setState({ added: true });
                    this.setState({ variant: "success" })
                    this.setState({ category_name: "" })
                    this.setState({ description: "" })
                    return response.json();
                }
            })
            .then(data => {
                this.setState({ message: data.detail })

            })
            .catch(error => {
                this.setState({ errorMessage: error.toString() });
                console.error('There was an error!', error);
            });
    }

    render() {
        return (
            <div className="Login">
                <Container fluid="md">
                    <Form onSubmit={this.handleSubmit}>
                        <Form.Group size="lg" controlId="product_name">
                            <Form.Label> Category Name </Form.Label>
                            <Form.Control
                                autoFocus
                                type="text"
                                value={this.state.product_name}
                                onChange={this.setCategoryName}
                            />
                        </Form.Group>

                        <Form.Group size="lg" controlId="category_description">
                            <Form.Label> Description </Form.Label>
                            <Form.Control
                                type="text"
                                value={this.state.description}
                                onChange={this.setDescription}
                            />
                        </Form.Group>

                        <Form.Select aria-label="Floating label select example">
                            <option>Open this select menu</option>
                            <option value="1">One</option>
                            <option value="2">Two</option>
                            <option value="3">Three</option>
                        </Form.Select>
                        

                        <Form.Group controlId="formFileSm" className="mb-3">
                            <Form.Label>Small file input example</Form.Label>
                            <Form.Control type="file" size="sm" />
                        </Form.Group>

                        <Button block type="submit" disabled={!(this.state.category_name && this.state.description)}>
                            Save Details
                        </Button>
                    </Form>

                    <Alert show={this.state.added} variant={this.state.variant}>
                        <Alert.Heading>{this.state.message}</Alert.Heading>
                        <hr />
                        <div className="d-flex justify-content-end">
                            <Button onClick={() => this.setAdded(false)} variant="outline-success">
                                Close!
                            </Button>
                        </div>
                    </Alert>

                </Container>
            </div>
        );
    }
}

export default AddProduct;
