import React, { Component } from 'react';
import Form from "react-bootstrap/Form"
import Button from "react-bootstrap/Button"
import { Container } from 'react-bootstrap';
import './Login.css';
import { loginUser } from './actions/login'
import PropTypes from 'prop-types'
import { Redirect } from 'react-router';
export default class Login extends Component{

    static propTypes = {
        setUserDetails: PropTypes.func.isRequired,
    };
    
    static isAuthenticated() {
        const token = JSON.parse(localStorage.getItem('token'))
        if (token) return true;
    }

    constructor(props) {
        super(props)

        this.state = {
            username : "",
            password : ""
        }

        this.setUsername = this.setUsername.bind(this);
        this.setPassword = this.setPassword.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    setUsername(event) {
        this.setState({ username: event.target.value });
    }

    setPassword(event) {
        this.setState({ password: event.target.value });
    }

    validateForm() {
        return this.state.username.length > 0 && this.state.password.length > 0
    }

    handleSubmit(event) {
        event.preventDefault();
        loginUser({ username: this.state.username, password: this.state.password });
        if(localStorage.getItem('token')){
            this.props.setUserDetails(localStorage.getItem('token'))
        }
    }
    render(){

        const { from } = this.props.location || { from: { pathname: '/app' } }; // eslint-disable-line

        // cant access login page while logged in
        if (Login.isAuthenticated()) {
            return (
                <Redirect to={from} />
            );
        }

        return (
            <div className="Login">
                <Container fluid="md">
    
                    <Form onSubmit={this.handleSubmit}>
                        <Form.Group size="lg" controlId="username">
                            <Form.Label> username </Form.Label>
                            <Form.Control
                                autoFocus
                                type="email"
                                value={this.state.username}
                                onChange={this.setUsername}
                            />
                        </Form.Group>
    
                        <Form.Group size="lg" controlId="password">
                            <Form.Label> Password </Form.Label>
                            <Form.Control
                                type="password"
                                value={this.state.password}
                                onChange={this.setPassword}
                            />
                        </Form.Group>
                        <Form.Group size="lg">
                            <Button block type="submit" disabled={!this.validateForm}>
                                Login
                            </Button>
                        </Form.Group>
                    </Form>
                </Container>
            </div>
        );
    }
}
