import React from 'react';
import { BrowserRouter as Router, Route, Switch} from 'react-router-dom';
import './App.css';
import jwt_decode from "jwt-decode";
import Login from './components/Login';
import Home from './components/Home';
import About from './components/About';
import Logout from './components/Logout';
import Header from './components/Header';
import Product from './components/Product';
import Cart from './components/Cart';

function setToken(userToken) {
  sessionStorage.setItem('user', JSON.stringify(userToken.access_token));
}

function getToken() {
  const tokenString = sessionStorage.getItem('user');

  return JSON.parse(tokenString);
}

function setUserDetails(token, username){
  fetch(`http://localhost:8000/user?email=${username}`, {
          method: 'GET',
          headers: {
              'accept': 'application/json',
              'Content-Type': 'application/json',
              Authorization: "Bearer " + token.replace(/^"(.*)"$/, '$1')
          },
      })
      .then(async response => {
          const data = await response.json();
          sessionStorage.setItem('user_details', JSON.stringify(data));
      })
      .catch(error => {
          this.setState({ errorMessage: error.toString() });
          console.error('There was an error!', error);
      });
}

function App() {
  const token = getToken()

  if (!token) {
    return <Login setToken={setToken} />
  }

  var decoded_jwt = jwt_decode(token);
  setUserDetails(token, decoded_jwt.sub);

  return (
    <div className="App">
      <Router>
        <Header />
        <Switch>
          <Route path="/home"> <Home /> </Route>
          <Route path="/profile"> <About /> </Route>
          <Route path="/product/:category_id"> <Product /> </Route>
          <Route path="/cart"> <Cart /> </Route>
          <Route path="/logout"> <Logout /> </Route>
        </Switch>
      </Router>
    </div>
  );
}

export default App;
