import React, { Component } from 'react';
import Form from "react-bootstrap/Form"
import Button from "react-bootstrap/Button"
import { Accordion, Alert, Card, Col, Container, Row, useAccordionButton } from 'react-bootstrap';


function CustomToggle({ children, eventKey }) {
    const decoratedOnClick = useAccordionButton(eventKey);

    return (
        <Button
            type="button"
            variant="light"
            onClick={decoratedOnClick}
        >
            {children}
        </Button>
    );
}


class AddCategory extends Component {
    constructor(props) {
        super(props)

        this.state = {
            category_name: "",
            description: "",
            added: false,
            variant: "",
            message: ""
        }

        this.setCategoryName = this.setCategoryName.bind(this);
        this.setDescription = this.setDescription.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.setAdded = this.setAdded.bind(this);
    }

    setCategoryName(event) {
        this.setState({ category_name: event.target.value });
    }

    setDescription(event) {
        this.setState({ description: event.target.value });
    }

    setAdded(value) {
        this.setState({ added: value });
    }
    handleSubmit(event) {
        event.preventDefault()
        this.addCategory({ name: this.state.category_name, description: this.state.description });
    }

    async addCategory(details) {
        const token = this.props.token

        return fetch('http://localhost:8000/category/add', {
            method: 'POST',
            headers: {
                'accept': 'application/json',
                'Content-Type': 'application/json',
                Authorization: "Bearer " + token.replace(/^"(.*)"$/, '$1')
            },
            body: JSON.stringify(details)
        })
            .then(response => {
                if (!response.ok) {
                    this.setState({ added: true });
                    this.setState({ variant: "danger" })
                    return response.json();
                }
                else {
                    this.setState({ added: true });
                    this.setState({ variant: "success" })
                    this.setState({ category_name: "" })
                    this.setState({ description: "" })
                    return response.json();
                }
            })
            .then(data => {
                this.setState({ message: data.detail })

            })
            .catch(error => {
                this.setState({ errorMessage: error.toString() });
                console.error('There was an error!', error);
            });
    }

    render() {
        return (
            <div className="Login">
                <Container fluid="md">


                <Accordion defaultActiveKey="0" flush>
  <Accordion.Item eventKey="0">
    <Accordion.Header>Accordion Item #1</Accordion.Header>
      <Accordion.Body>
      Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod
      tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim
      veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea
      commodo consequat. Duis aute irure dolor in reprehenderit in voluptate
      velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat
      cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id
      est laborum.
    </Accordion.Body>
  </Accordion.Item>
  <Accordion.Item eventKey="1">
    <Accordion.Header>Accordion Item #2</Accordion.Header>
    <Accordion.Body>
      Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod
      tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim
      veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea
      commodo consequat. Duis aute irure dolor in reprehenderit in voluptate
      velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat
      cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id
      est laborum.
    </Accordion.Body>
  </Accordion.Item>
</Accordion>





                    <Accordion defaultActiveKey="0" flush>
                        <Accordion.Item eventKey="0">
                            <Button>Click me!</Button>
                            <Accordion.Body>
                                <Form onSubmit={this.handleSubmit}>
                                    <Form.Group size="lg" controlId="category_name" className="mb-3">
                                        <Form.Label> Category Name </Form.Label>
                                        <Form.Control
                                            autoFocus
                                            type="text"
                                            value={this.state.category_name}
                                            onChange={this.setCategoryName}
                                        />
                                    </Form.Group>

                                    <Form.Group size="lg" controlId="category_description" className="mb-3">
                                        <Form.Label> Description </Form.Label>
                                        <Form.Control
                                            type="text"
                                            value={this.state.description}
                                            onChange={this.setDescription}
                                        />
                                    </Form.Group>
                                    <center>
                                        <Button className="mb-3" block type="submit" disabled={!(this.state.category_name && this.state.description)}>
                                            Save Details
                                        </Button>
                                    </center>
                                </Form>
                            </Accordion.Body>
                        </Accordion.Item>
                    </Accordion>

                    <Row className="justify-content-md-center">
                        <Col md="auto">
                            <Alert show={this.state.added}
                                variant={this.state.variant}
                                onClick={() => this.setAdded(false)}
                                dismissible
                                size="sm"
                            >
                                <center>
                                    {this.state.message}
                                </center>
                            </Alert>
                        </Col>
                    </Row>
                </Container>
            </div>
        );
    }
}

export default AddCategory;
