import React, { Component } from 'react';
import Form from "react-bootstrap/Form"
import Button from "react-bootstrap/Button"
import { Container } from 'react-bootstrap';

class AddCategory extends Component {
    constructor(props) {
        super(props)

        this.state = {
            category_name: "",
            description: "",
        }

        this.setCategoryName = this.setCategoryName.bind(this);
        this.setDescription = this.setDescription.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    setCategoryName(event) {
        this.setState({ category_name: event.target.value });
    }

    setDescription(event) {
        this.setState({ description: event.target.value });
    }
    handleSubmit(event){
        event.preventDefault()
        this.addCategory({ name: this.state.category_name, description: this.state.description });
    }

    async addCategory(details){
        const qs = require('qs');
        return fetch('http://localhost:8000/category/add', {
            method: 'POST',
            headers: {
                'accept': 'application/json',
                'Content-Type': 'application/x-www-form-urlencoded'
            },
            body: qs.stringify(credentials)
        })
        .then(response => {
            if(!response.ok){
                this.setState({ authenticated : false });
            }
            else{
                this.setState({ authenticated : true });
                return response.json();
            }
        })
        .then(data => {
            if(this.state.authenticated){
                localStorage.setItem('authenticated', true)
                this.setState({ access_token : data.access_token });
            }
        })
    }
    
    render() {
        return (
            <div className="Login">
                <Container fluid="md">
                    <Form onSubmit={this.handleSubmit}>
                        <Form.Group size="lg" controlId="category_name">
                            <Form.Label> Category Name </Form.Label>
                            <Form.Control
                                autoFocus
                                type="text"
                                value={this.state.category_name}
                                onChange={this.setCategoryName}
                            />
                        </Form.Group>

                        <Form.Group size="lg" controlId="category_description">
                            <Form.Label> Description </Form.Label>
                            <Form.Control
                                type="text"
                                value={this.state.description}
                                onChange={this.setDescription}
                            />
                        </Form.Group>
                        <Button block type="submit" disabled={!(this.state.category_name && this.state.description)}>
                            Save Details
                        </Button>
                    </Form>
                </Container>
            </div>
        );
    }
}

export default AddCategory;
