import React from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect} from 'react-router-dom';
import './App.css';
import Home from './components/Home';
import About from './components/About';
import Logout from './components/Logout';
import Header from './components/Header';
import Product from './components/Product';
import Cart from './components/Cart';
import Login from './components/Login';

const PrivateRoute = ({component, ...rest }) => {
  if (!Login.isAuthenticated(JSON.parse(localStorage.getItem('authenticated')))) {
      return (<Redirect to="/login"/>)
  } else {
      return ( // eslint-disable-line
          <Route {...rest} render={props => (React.createElement(component, props))}/>
      );
  }
};

function getToken() {
  const tokenString = sessionStorage.getItem('user');

  return JSON.parse(tokenString);
}

function setUserDetails(token, username){
  fetch(`http://localhost:8000/user?email=${username}`, {
          method: 'GET',
          headers: {
              'accept': 'application/json',
              'Content-Type': 'application/json',
              Authorization: "Bearer " + token.replace(/^"(.*)"$/, '$1')
          },
      })
      .then(async response => {
          const data = await response.json();
          sessionStorage.setItem('user_details', JSON.stringify(data));
      })
      .catch(error => {
          this.setState({ errorMessage: error.toString() });
          console.error('There was an error!', error);
      });
}

function App() {

  if(localStorage.getItem('token')){
    return (<Redirect to="/login"/>)
  }
  return (
    <div className="App">
      <Router>
        <Header />
        <Switch>
          {/* <PrivateRoute path="/home" component={Home} /> */}
          <PrivateRoute path='/' component={Home} />
          <Route path="/"> <Home/> </Route>
          <Route path="/profile"> <About /> </Route>
          <Route path="/product/:category_id"> <Product /> </Route>
          <Route path="/cart"> <Cart /> </Route>
          <Route path="/login"> <Cart /> </Route>
          <Route path="/logout"> <Logout /> </Route>
        </Switch>
      </Router>
    </div>
  );
}

export default App;
