import React, { Component } from 'react';
import Form from "react-bootstrap/Form"
import Button from "react-bootstrap/Button"
import { Alert, Container } from 'react-bootstrap';

class AddCategory extends Component {
    constructor(props) {
        super(props)

        this.state = {
            category_name: "",
            description: "",
            added: false,
            variant: "",
            message: ""
        }

        this.setCategoryName = this.setCategoryName.bind(this);
        this.setDescription = this.setDescription.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.setAdded = this.setAdded.bind(this);
    }

    setCategoryName(event) {
        this.setState({ category_name: event.target.value });
    }

    setDescription(event) {
        this.setState({ description: event.target.value });
    }

    setAdded(value){     
        this.setState({ added: value });
    }
    handleSubmit(event) {
        event.preventDefault()
        this.addCategory({ name: this.state.category_name, description: this.state.description });
    }

    async addCategory(details) {
        const token = this.props.token

        return fetch('http://localhost:8000/category/add', {
            method: 'POST',
            headers: {
                'accept': 'application/json',
                'Content-Type': 'application/json',
                Authorization: "Bearer " + token.replace(/^"(.*)"$/, '$1')
            },
            body: JSON.stringify(details)
        })
            .then(response => {
                if (!response.ok) {
                    this.setState({ added: true });
                    this.setState({variant: "danger"})
                    return response.json();
                }
                else {
                    this.setState({ added: true });
                    this.setState({variant: "success"})
                    this.setState({category_name : ""})
                    this.setState({description: ""})
                    return response.json();
                }
            })
            .then(data =>{
                this.setState({message: data.detail})
                
            })
            .catch(error => {
                this.setState({ errorMessage: error.toString() });
                console.error('There was an error!', error);
            });
    }

    render() {
        return (
            <div className="Login">
                <Container fluid="md">
                    <Form onSubmit={this.handleSubmit}>
                        <Form.Group size="lg" controlId="category_name" className="mb-3">
                            <Form.Label> Category Name </Form.Label>
                            <Form.Control
                                autoFocus
                                type="text"
                                value={this.state.category_name}
                                onChange={this.setCategoryName}
                            />
                        </Form.Group>

                        <Form.Group size="lg" controlId="category_description" className="mb-3">
                            <Form.Label> Description </Form.Label>
                            <Form.Control
                                type="text"
                                value={this.state.description}
                                onChange={this.setDescription}
                            />
                        </Form.Group>
                        <center>
                            <Button block type="submit" disabled={!(this.state.category_name && this.state.description)}>
                                Save Details
                            </Button>
                        </center>
                    </Form>
        
                    <Alert show={this.state.added} variant={this.state.variant}>
                        {this.state.message}
                        <span className="d-flex justify-content-end">
                            <Button onClick={() => this.setAdded(false)} variant="outline-success">
                                Close!
                            </Button>
                        </span>
                    </Alert>

                </Container>
            </div>
        );
    }
}

export default AddCategory;
